const colors = {
  gray: "#D8D6D6",
  black: "#000000",
  yellow: "#FFF5A2",
  purple: "#DDC2FF",
};

export type CombinedShapeProps = {
  shape: WishShape;
  color: WishColor;
  accent: WishColor;
  width?: "sm";
};

const components: Record<WishShape, FunctionComponent<ConcreteShapeProps>> = {
  "shape-1": CombinedShape1,
  "shape-2": CombinedShape2,
  "shape-3": CombinedShape3,
  "shape-4": CombinedShape4,
  "shape-5": CombinedShape5,
  "shape-6": CombinedShape6,
  "shape-7": CombinedShape7,
  "shape-8": CombinedShape8,
  "shape-9": CombinedShape9,
  "shape-10": CombinedShape10,
  "shape-11": CombinedShape11,
  "shape-12": CombinedShape12,
};

export function CombinedShape({ shape, accent, color, width }: CombinedShapeProps) {
  const Component = components[shape];
  const hexColor = colors[color];
  const hexAccent = colors[accent];

  return <Component color={hexColor} accent={hexAccent} width={width} />;
}

